mapper.select:
SELECT attach_no, board_no, userfilename, savedfilename, downloadcount
FROM BoardAttach
WHERE board_no = #{boardNo}

SELECT attach_no, board_no, userfilename, savedfilename, downloadcount
FROM BoardAttach
WHERE attach_no = #{attachNo}

mapper.delete:
UPDATE Board 
SET deleted = TRUE 
WHERE board_no = #{boardNo}

mapper.update:
UPDATE Board
SET title = #{title}, content =#{content}
WHERE board_no = #{boardNo}

UPDATE Board 
SET readcount = readcount + 1 
WHERE board_no = #{boardNo}
