mapper.delete:
UPDATE BoardComment
SET comment_deleted = TRUE
WHERE board_comment_no = #{commentNo}

mapper.update:
UPDATE BoardComment
SET comment_content = #{content}
WHERE board_comment_no = #{commentNo}

UPDATE BoardComment
SET groupno = #{groupNo}
WHERE board_comment_no = #{commentNo}


UPDATE BoardComment
SET step = step + 1
WHERE groupno = #{groupNo} AND step > #{step}
